#include <vector>
#include <numeric>

using Matrix = std::vector<std::vector<int>>;

void matrix_multiply(const Matrix& A, Matrix& result) {
    int n = A.size();
    result.resize(n, std::vector<int>(n));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            result[i][j] = std::inner_product(A[i].begin(), A[i].end(), A[j].begin(), 0);
        }
    }
}
