#include <vector>
#include <omp.h>

using Array = std::vector<int>;

int array_sum(const Array& arr) {
    int sum = 0;
    #pragma omp parallel for reduction(+:sum)
    for (size_t i = 0; i < arr.size(); ++i) {
        sum += arr[i];
    }
    return sum;
}

#include <vector>
#include <omp.h>

using Matrix = std::vector<std::vector<int>>;

void matrix_multiply(const Matrix& A, Matrix& result) {
    int n = A.size();
    int m = A[0].size();
    result.resize(n, std::vector<int>(n, 0));

    #pragma omp parallel for collapse(2)
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            for (int k = 0; k < m; ++k) {
                result[i][j] += A[i][k] * A[j][k];
            }
        }
    }
}
