#include <vector>
#include <omp.h>

// Define the array type
using Array = std::vector<long long>;

long long array_sum(const Array& arr) {
    long long sum = 0;
    #pragma omp parallel for reduction(+:sum)
    for (size_t i = 0; i < arr.size(); ++i) {
        sum += arr[i];
    }
    return sum;
}

#include <vector>
#include <omp.h>

using Matrix = std::vector<std::vector<int>>;

void matrix_multiply(const Matrix& A, Matrix& result) {
    int n = A.size();
    result.resize(n, std::vector<int>(n, 0));

    #pragma omp parallel for collapse(2)
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            int sum = 0;
            for (int k = 0; k < n; ++k) {
                sum += A[i][k] * A[j][k];
            }
            result[i][j] = sum;
        }
    }
}

#include <omp.h>
#include <vector>
#include <queue>

void bfs(const Graph& graph, int start, std::vector<int> & result) {
    std::vector<bool> visited(graph.numVertices, false);
    std::queue<int> q;
    q.push(start);
    visited[start] = true;
    result[start] = 0;

    while (!q.empty()) {
        int current = q.front();
        q.pop();

        #pragma omp parallel for
        for (int i = graph.offset[current]; i < graph.offset[current + 1]; ++i) {
            int neighbor = graph.edges[i];
            if (!visited[neighbor]) {
                #pragma omp critical
                {
                    if (!visited[neighbor]) { // Double-check within critical section
                        visited[neighbor] = true;
                        result[neighbor] = result[current] + 1;
                        q.push(neighbor);
                    }
                }
            }
        }
    }
}
